import React from "react";
import styled from "styled-components";

const Container = styled.div`
  background: #fff;
  border-radius: 4px;
  padding: 24px 32px;
  transition: all 0.2s;
  min-height: 150px;
  max-width: 500px;
  margin: 0 auto;
`;

export default class FriendList extends React.Component {
  constructor(props) {
    super(props);
  var number=0;
  const listItems = props.friends.map((friend) =>
  <li key={number++}>{friend.name}</li>
);
this.state = {friends: listItems};
}

  render() {
    return <Container>{this.state.friends}</Container>;
  }
}
