import React from "react";
import styled from "styled-components";

const Container = styled.div`
  background: #fff;
  border-radius: 4px;
  padding: 24px 32px;
  transition: all 0.2s;
  min-height: 150px;
  max-width: 500px;
  margin: 0 auto;
`;
