  constructor(props) {
    super(props);
  var number=0;
  const listItems = props.friends.map((friend) =>
  <li key={number++}>{friend.name}</li>
);
this.state = {friends: listItems};

}
  onChange = event => {
    console.log('gg');
  }
  render() {
    return  <Container>
      <div className="header">Friends</div>
      <input className="filter-input" placeholder="Search..."/>
      <div className="friend-list">
      {this.state.friends}
      </div>
      </Container>;
  }
}
